      if (response.ok) {
        response.json().then(function (informacao) {
          console.log(`Dados recebidos: ${JSON.stringify(informacao)}`);
          informacao.reverse();

          plotarAtributos(informacao);
        });
      } else {
        console.error('Nenhum dado encontrado ou erro na API');
      }
